import React, { useState } from "react";
import team1 from "../assets/images/team1.jpeg";
import team2 from "../assets/images/team2.jpeg";
import team3 from "../assets/images/team3.jpeg";
import team4 from "../assets/images/team4.jpeg";
import team5 from "../assets/images/team5.jpeg";
import team6 from "../assets/images/team6.jpeg";
import team7 from "../assets/images/team7.jpeg";
import team8 from "../assets/images/team8.jpeg";
import team9 from "../assets/images/team9.jpeg";
import team10 from "../assets/images/team10.jpeg";
import teamBg from "../assets/images/team-bg.jpeg"; // background image

const teamMembers = [
  { name: "Member 1", role: "Founder & Environmental Activist", image: team1 },
  { name: "Member 2", role: "Project Coordinator", image: team2 },
  { name: "Member 3", role: "Field Officer", image: team3 },
  { name: "Member 4", role: "Communication Head", image: team4 },
  { name: "Member 5", role: "Tree Conservationist", image: team5 },
  { name: "Member 6", role: "Volunteer Manager", image: team6 },
  { name: "Member 7", role: "Operations Lead", image: team7 },
  { name: "Member 8", role: "CSR Coordinator", image: team8 },
  { name: "Member 9", role: "Data Analyst", image: team9 },
  { name: "Member 10", role: "Field Volunteer", image: team10 },
];

const OurTeam = () => {
  const [activeIndex, setActiveIndex] = useState(2); // Center index

  React.useEffect(() => {
    const timer = setInterval(() => {
      setActiveIndex((prevIndex) =>
        prevIndex === teamMembers.length - 1 ? 0 : prevIndex + 1
      );
    }, 3000);
    return () => clearInterval(timer);
  }, []);

  const getVisibleMembers = () => {
    let visible = [];

    for (let i = -2; i <= 2; i++) {
      const index = (activeIndex + i + teamMembers.length) % teamMembers.length;
      visible.push({
        ...teamMembers[index],
        position: i,
      });
    }

    return visible;
  };
  return (
    <div className="text-gray-800">
      {/* Header with background image */}
      <section
        className="text-white text-center w-full py-20 bg-cover bg-center bg-no-repeat relative"
        style={{ backgroundImage: `url(${teamBg})` }}
      >
        <div className="absolute inset-0 bg-black bg-opacity-50"></div>
        <div className="relative z-10">
          <h1 className="text-4xl font-bold mb-2">Meet Our Team</h1>
          <p className="text-lg">The passionate people behind Sahyadri Devrai</p>
        </div>
      </section>

      {/* Grid with central highlight and side blur */}
      <div className="relative w-full max-w-7xl overflow-hidden p-5">
        <div className="flex justify-center gap-6 items-center">
          {getVisibleMembers().map((member, index) => {
            let blurClass = "";
            if (Math.abs(member.position) === 2) blurClass = "blur-sm opacity-40 scale-90";
            else if (Math.abs(member.position) === 1) blurClass = "blur-[2px] opacity-60 scale-95";
            else blurClass = "opacity-100 scale-100 z-10";

            return (
              <div
                key={index}
                className={`transition-all duration-700 ease-in-out transform ${blurClass}`}
              >
                <div className="bg-white text-gray-800 rounded-xl shadow-xl p-6 w-64 text-center">
                  <img
                    src={member.image}
                    alt={member.name}
                    className="w-24 h-24 rounded-full mx-auto mb-3 object-cover border-4 border-green-300"
                  />
                  <h4 className="text-lg font-bold">{member.name}</h4>
                  <p className="text-sm text-gray-600">{member.role}</p>
                </div>
              </div>
            );
          })}
        </div>
      </div>
    </div>
  );
};

export default OurTeam;
